const initState={token:localStorage.getItem("token"),isAuthenticated:null,isLoading:!1,user:null},authReducer=(state=initState,action)=>{switch(action.type){case"USER_LOADING":return{...state,isLoading:!0};case"USER_LOADED":return{...state,isAuthenticated:!0,isLoading:!1,user:action.payload};case"LOGIN_SUCCESS":case"REGISTER_SUCCESS":return localStorage.setItem("token",action.payload.token),{...state,...action.payload,isAuthenticated:!0,isLoading:!1};case"AUTH_ERROR":case"LOGIN_FAILED":case"LOGOUT_SUCCESS":case"REGISTER_FAILED":return localStorage.removeItem("token"),{...state,token:null,user:null,isAuthenticated:!1,isLoading:!1};default:return state}};export default authReducer;