const express=require("express"),router=express.Router(),bcrypt=require("bcryptjs"),jwt=require("jsonwebtoken"),auth=require("../../middleware/auth"),User=require("../../models/User");router.post("/",(req,res)=>{const{name:name,password:password}=req.body;if(!name||!password)return res.status(400).json({msg:"Please input all fields!"});User.findOne({name:name}).then(user=>{if(!user)return res.status(400).json({msg:"User does not exist."});bcrypt.compare(password,user.password).then(isMatch=>{if(!isMatch)return res.status(400).json({msg:"Invalid password."});jwt.sign({id:user.id},process.env.JWT_SECRET,{expiresIn:3600},(err,token)=>{if(err)throw err;res.json({token:token,user:{id:user.id,name:user.name,email:user.email}})})})})}),router.get("/user",auth,(req,res)=>{User.findById(req.user.id).select("-password").then(user=>res.json(user))}),module.exports=router;